import React, { Component } from 'react'
import jwt_decode from 'jwt-decode'
import CardItem from './CardItem';
import './Products.css';
class Profile extends Component {
  constructor() {
    super()
    this.state = {
      username: '',
      email: '',
      role: '',
      errors: {},
      tutorials:{}
    }
  }

  componentDidMount() {
    // const token = localStorage.usertoken
    // const decoded = jwt_decode(token)
    // this.setState({
    //   username: decoded.identity.username,
    //   email: decoded.identity.email,
    //   role: decoded.identity.role
    // })
    fetch('http://localhost:5000/teacher_profile', {
      method: 'POST',
      body:JSON.stringify({'username':this.state.username}),
      headers: new Headers({
        "content-type": "application/json"
      }),
    }).then((response) => {
      response.json().then((body) => {
        this.setState({tutorials:body});
        console.log(this.state.tutorials);
        // this.setState({onsubmit:true});
    });
  })
  }

  componentWillMount(){
    const token = localStorage.usertoken
    const decoded = jwt_decode(token)
    this.setState({
      username: decoded.identity.username,
      email: decoded.identity.email,
      role: decoded.identity.role
    })
}

  render() {
    return (
      <div className="container">
        <center>
        <div className="">
          <div className="col-sm-8 mx-auto">
            <center><h1 className="text-body">PROFILE</h1></center>
          </div>
          <table className="table col-md-6 mx-auto">
            <tbody className="text-body">
              <tr>
                <td>Username</td>
                <td>{this.state.username}</td>
              </tr>
              <tr>
                <td>Role</td>
                <td>{this.state.role}</td>
              </tr>
            </tbody>
          </table>
        </div>
        </center>
        <p>{this.state.tutorials.toString()}</p>
        <br></br><br></br>
        <div className='cards__wrapper'>
        {Object.keys(this.state.tutorials).map((tutorial,index) => (
            <ul className='cards__items'>
              <CardItem
                src='https://youthincmag.com/wp-content/uploads/2019/04/Software-Engineering-696x373.jpg'
                text={tutorial[name]}
                label={tutorial['label']}
                path='/'
              />
            </ul>
        ))}
      </div>
      </div>
    )
  }
}

export default Profile
