import React from 'react';
import PropTypes from 'prop-types';
import Question from './Question';
import QuestionCount from './QuestionCount';
import AnswerOption from './AnswerOption';
import { CSSTransitionGroup } from 'react-transition-group';

function Completed(props) {
    console.log(props);
    function shuffle(array) {
        console.log("hi");
        var currentIndex = array.length, temporaryValue, randomIndex;
      
        // While there remain elements to shuffle...
        while (0 !== currentIndex) {
      
          // Pick a remaining element...
          randomIndex = Math.floor(Math.random() * currentIndex);
          currentIndex -= 1;
      
          // And swap it with the current element.
          temporaryValue = array[currentIndex];
          array[currentIndex] = array[randomIndex];
          array[randomIndex] = temporaryValue;
        }
      
        return array;
      }
    function renderAnswerOptions(question,key) {
        return (
          <AnswerOption
            key={key}
            answerContent={key}
            // answerType={key.type}
            answer={question["user_answer"]}
            correct_answer={question["correct_answer"]}
            onAnswerSelected={props.onAnswerSelected}
            status="taken"
          />
        );
      }
        return (
            props.questions.map((question,index)=>{
                return (
      <CSSTransitionGroup
      className="container"
      component="div">
        <div key={index+1}>
          <QuestionCount
            counter={index+1}
            total={props.questions.length}
          />
          <Question content={question["question"]} />
          <ul className="answerOptions">
            {shuffle(question["answers"]).map(function (key) {
             return renderAnswerOptions(question, key);})}
          </ul>
          <br></br>
          {question["user_answer"]!==question["correct_answer"]? <h2>Correct answer is {question["correct_answer"]}</h2>:false}
        </div>
        </CSSTransitionGroup>)
    }
    ));
}
  
  export default Completed;
