import React from 'react';

class Content extends React.Component{
    constructor(props){
        super(props);
        this.state=props.heading;
    }
    render(){
        const Content_data=({data}) =>(<div><h1>{data["heading"]}</h1><br></br>
        <p>{data["content"]}</p></div>);
        const TreeRecursive = ({ data }) => {
            return data.map(item => {
              if (item["children"].length == 0) {
                return <Content_data data={item}/>
              }
              else {
                return (
                  <SubMenu title={item["heading"]} onClick={() => this.print(item["pgno"])}>
                    <TreeRecursive data={item["children"]} />
                  </SubMenu>
                );
              }
            });
          };
        return(
            <div className="container">
                <h1>{this.state["heading"]}</h1>
                <br></br>
                <p>{this.state["content"]}</p>
            </div>
        )
    }
}
export default Content;
