import React from 'react';
import './Products.css';
import CardItem from './CardItem';
import Tutorial from './Tutorial';
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom';
import Profile from './Profile';
import Assessment from './Assessment';
import Upload from './Upload';
import Sidebar from '../Sidebar';
import '../Sidebar.css';
import { ProSidebar, Menu, MenuItem, SubMenu } from 'react-pro-sidebar';
import 'react-pro-sidebar/dist/css/styles.css';
import Iframe from 'react-iframe'
class ppt extends React.Component {
    constructor(){
        super();
        this.openFullscreen=this.openFullscreen.bind(this);
        this.print=this.print.bind(this);
        this.state={
			hierarchy:[{'heading': 'What is Information Retrieval?', 'level': 1, 'pgno': 1, 'children': []}, {'heading': 'What is an IR Model? ', 'level': 1, 'pgno': 3, 'children': []}, {'heading': 'Components of Information Retrieval/ IR Model ', 'level': 1, 'pgno': 4, 'children': [{'heading': ' Acquisition', 'level': 2, 'pgno': 5, 'children': []}, 
            {'heading': ' Representation', 'level': 2, 'pgno': 6, 'children': []}, {'heading': ' File Organization', 'level': 2, 'pgno': 7, 'children': []}, {'heading': ' Query', 'level': 2, 'pgno': 8, 'children': []}]}, {'heading': 'User Interaction With Information Retrieval System ', 'level': 1, 'pgno': 9, 'children': []}],
            src:"http://localhost:5000/return-files1?pptpath=D:/College/Capstone Project/Final/backend/Create-tutorials-from-text-file/scripts/ppt_trial.pdf"
		}
    }
    openFullscreen(ev) {
        ev.preventDefault();
        // console.log(elem.parentNode.childNodes)
        // elem=elem.parentNode.childNodes[3];
        console.log("here");
        var elem=document.getElementById("button1");
        if (elem.requestFullscreen) {
          elem.requestFullscreen();
        } else if (elem.webkitRequestFullscreen) { /* Safari */
          elem.webkitRequestFullscreen();
        } else if (elem.msRequestFullscreen) { /* IE11 */
          elem.msRequestFullscreen();
        }
      } 
    print(){
        // var x=document.getElementById("button1");
        // console.log(x);
        // document.getElementById("button1").src="http://localhost:5000/return-files1?pptpath=D:/College/Capstone Project/Final/backend/Create-tutorials-from-text-file/scripts/ppt_trial.pdf#page=9";
        this.setState({src:"http://localhost:5000/return-files1?pptpath=D:/College/Capstone Project/Final/backend/Create-tutorials-from-text-file/scripts/ppt_trial.pdf#page=9"})
        // document.getElementById("button1").contentDocument.location.reload(true);
    }
    render(){
        const TreeRecursive = ({ data }) => {
            // loop through the data
            return data.map(item => {
              // if its a file render <File />
              if (item["children"].length == 0) {
                return <MenuItem onClick={this.print}>{item["heading"]}</MenuItem>;
              }
              // if its a folder render <Folder />
              else {
                return (
                  <SubMenu title={item["heading"]}>
                    {/* Call the <TreeRecursive /> component with the current item.childrens */}
                    <TreeRecursive data={item["children"]} />
                  </SubMenu>
                );
              }
            });
          };
        console.log(document.getElementById("button1"));
  return (
  	 <div class="row">
	     <div class="col-2">
	     <ProSidebar>
		<Menu iconShape="square" id="sidebar">
	<TreeRecursive data={this.state.hierarchy}/>
	</Menu>
	</ProSidebar>
	     </div> 
	     <div class="col-8"> 
               {/* <center>
      <div class="embed-responsive embed-responsive-16by9">
	                        
	                       	
                            <a  class="elem-fullscreen-link" onClick={this.openFullscreen} id="button">
                              <span class="fa fa-arrows-alt" aria-hidden="true"></span>
                              </a>
                          <iframe class="embed-responsive-item elem-fullscreen" src={this.state.src} allowfullscreen="" width="100%" height="100%" id="button1">
                          </iframe>
                          </div>
	      <hr/>
        </center> */}
    <Iframe url={{url:this.state.src}}
        width="100%"
        height="100%"
        id="myId"
        className="myClassname"
        display="initial"
        position="relative"/>
	    </div>
        {/* <embed id="button1" src={this.state.src} allowfullscreen="" width="100%" height="100%" id="button1"></embed>
        </center></div> */}
	    <div class="col-2">
	    <br/>
	      <ul className='cards__items'>
            <CardItem
              src='http://i.ytimg.com/vi/BdhErO4a9XE/maxresdefault.jpg'
              text='Learn about the various events in PES University'
              path='https://events.pes.edu/'
            />
           </ul>
           <ul>
            <CardItem
              src='https://minutes.co/wp-content/uploads/2019/06/research-and-scholarship.jpg'
              text='Learn about the ongoing Research in PES University'
              path='https://research.pes.edu/patents/'
            />
           </ul>
           <ul>
            <CardItem
              src='https://news.pes.edu/Uploads/20190704%20103742_1.jpg'
              text='Be Updated with the current news in PES University'
              path='https://news.pes.edu/'
            />
          </ul>
	    </div>
	  </div>
  	)
}
}

export default ppt;
