import React from 'react';
import './studentass.css';
import Question from './StudentAssessment/Question';
import Quiz from './StudentAssessment/Quiz';
class Sassessment extends React.Component{
    constructor(props) {
        super(props);
      
        this.state = {
          counter: 0,
          questionId: 1,
          question: 'rekjnvkejrv',
          answerOptions: ['kjernv','rjeign'],
          answer: 'rknvekrf',
          answersCount: {},
          result: '',
          mcq: this.props.location.mcq
        };
        this.handleAnswerSelected = this.handleAnswerSelected.bind(this);
      }
      handleAnswerSelected(event) {
        this.setUserAnswer(event.currentTarget.value);
        if (this.state.questionId < this.state.mcq.length) {
            setTimeout(() => this.setNextQuestion(), 300);
          } else {
            // do nothing for now
          }
      }
    render(){
        return (
            <Quiz
        answer={this.state.answer}
        answerOptions={this.state.answerOptions}
        questionId={this.state.questionId}
        question={this.state.question}
        questionTotal={this.state.mcq.length}
        onAnswerSelected={this.handleAnswerSelected}
      />
        );
    }
}

export default Sassessment;
