import React from 'react';
import './Products.css';
import CardItem from './CardItem';
import Tutorial from './Tutorial';
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom';
import Profile from './Profile';
import Assessment from './Assessment';
import Upload from './Upload';
import Sidebar from '../Sidebar';
export function openFullscreen(elem) {
    console.log(elem.parentNode.childNodes)
    elem=elem.parentNode.childNodes[3];
    if (elem.requestFullscreen) {
      elem.requestFullscreen();
    } else if (elem.webkitRequestFullscreen) { /* Safari */
      elem.webkitRequestFullscreen();
    } else if (elem.msRequestFullscreen) { /* IE11 */
      elem.msRequestFullscreen();
    }
  }
export default function ppt() {
    
  return (
  	 <div class="row">
	    <div class="col-2">
	    <Sidebar></Sidebar>
	    </div>
	    <div class="col-8">
      <center>
      <div class="embed-responsive embed-responsive-16by9">
	                        
	                       	
                            <a  class="elem-fullscreen-link" onclick="openFullscreen(this)">
                              <span class="fa fa-arrows-alt" aria-hidden="true"></span>
                              </a>
                          <iframe class="embed-responsive-item elem-fullscreen" src="http://localhost:5000/return-files1?pptpath=D:/College/Capstone Project/Final/backend/Create-tutorials-from-text-file/scripts/static/downloads/abstract_summary.pdf" allowfullscreen="" width="100%" height="auto">
                            {/* <!DOCTYPE html> */}
                            {/* <html><head></head><body style="height: 100%; width: 100%; overflow: hidden; margin:0px; background-color: rgb(82, 86, 89);"><embed name="4554881B4879FAE0AF3D5241613C0165" style="position:absolute; left: 0; top: 0;" width="100%" height="100%" src="about:blank" type="application/pdf" internalid="4554881B4879FAE0AF3D5241613C0165"></body></html> */}
                          </iframe>
                          </div>
	      <hr/>
        </center>
	    </div>
	    <div class="col-2">
	    <br/>
	      <ul className='cards__items'>
            <CardItem
              src='http://i.ytimg.com/vi/BdhErO4a9XE/maxresdefault.jpg'
              text='Learn about the various events in PES University'
              path='https://events.pes.edu/'
            />
           </ul>
           <ul>
            <CardItem
              src='https://minutes.co/wp-content/uploads/2019/06/research-and-scholarship.jpg'
              text='Learn about the ongoing Research in PES University'
              path='https://research.pes.edu/patents/'
            />
           </ul>
           <ul>
            <CardItem
              src='https://news.pes.edu/Uploads/20190704%20103742_1.jpg'
              text='Be Updated with the current news in PES University'
              path='https://news.pes.edu/'
            />
          </ul>
	    </div>
	  </div>
  	)
}
