import React from 'react';
import { ProSidebar, Menu, MenuItem, SubMenu } from 'react-pro-sidebar';
import { Link } from 'react-router-dom';
import 'react-pro-sidebar/dist/css/styles.css';
import { render } from '@testing-library/react';
import TreeMenu from 'react-simple-tree-menu';
import './Sidebar.css';
class Sidebar extends React.Component {
	constructor(){
		super()
		this.state={
			hierarchy:[{'heading': 'What is Information Retrieval?', 'level': 1, 'pgno': 1, 'children': []}, {'heading': 'What is an IR Model? ', 'level': 1, 'pgno': 3, 'children': []}, {'heading': 'Components of Information Retrieval/ IR Model ', 'level': 1, 'pgno': 4, 'children': [{'heading': ' Acquisition', 'level': 2, 'pgno': 5, 'children': []}, 
			{'heading': ' Representation', 'level': 2, 'pgno': 6, 'children': []}, {'heading': ' File Organization', 'level': 2, 'pgno': 7, 'children': []}, {'heading': ' Query', 'level': 2, 'pgno': 8, 'children': []}]}, {'heading': 'User Interaction With Information Retrieval System ', 'level': 1, 'pgno': 9, 'children': []}],
		}
		this.populate=this.populate.bind(this);
		this.submenu_populate=this.submenu_populate.bind(this);
	}
	submenu_populate(submenu,children){
		for(var i=0;i<children.length;i++){
			if(children[i].length>0){
				var submenu1=document.createElement("SubMenu")
				submenu1.title=children[i]["heading"];
				submenu.appendChild(this.submenu_populate(submenu1,children["children"]));
			}
			else{
				var subheading=document.createElement("MenuItem")
				subheading.innerHTML=children[i]["heading"];
				submenu.appendChild(subheading);
			}
		}
		return submenu;
	}
	populate(){
		var sidebar=document.getElementById("sidebar");
		for(var i=0;i<this.state.hierarchy.length;i++){
			var x=this.state.hierarchy[i];
			if(x["children"].length==0){
				var subheading=document.createElement("MenuItem")
				subheading.innerHTML=x["heading"];
				sidebar.appendChild(subheading);
			}
			else{
				var submenu=document.createElement("SubMenu")
				submenu.title=x["heading"];
				submenu.appendChild(this.submenu_populate(submenu,x["children"]))
				sidebar.appendChild(submenu);
			}
		}
	}
render(){
	function openFullscreen(elem) {
		console.log(elem.parentNode.childNodes)
		elem=elem.parentNode.childNodes[3];
		if (elem.requestFullscreen) {
		  elem.requestFullscreen();
		} else if (elem.webkitRequestFullscreen) { /* Safari */
		  elem.webkitRequestFullscreen();
		} else if (elem.msRequestFullscreen) { /* IE11 */
		  elem.msRequestFullscreen();
		}
	  }
	const TreeRecursive = ({ data }) => {
		// loop through the data
		return data.map(item => {
		  // if its a file render <File />
		  if (item["children"].length == 0) {
			return <MenuItem>{item["heading"]}</MenuItem>;
		  }
		  // if its a folder render <Folder />
		  else {
			return (
			  <SubMenu title={item["heading"]}>
				{/* Call the <TreeRecursive /> component with the current item.childrens */}
				<TreeRecursive data={item["children"]} />
			  </SubMenu>
			);
		  }
		});
	  };

	// this.populate();
return (
	// <TreeMenu data={this.state.data}></TreeMenu>
	<div>
	<ProSidebar>
		<Menu iconShape="square" id="sidebar">
	<TreeRecursive data={this.state.hierarchy}/>
	</Menu>
	</ProSidebar>
	  <div class="embed-responsive embed-responsive-16by9">
	                        
	                       	
	  <a  class="elem-fullscreen-link" onclick="openFullscreen(this)">
		<span class="fa fa-arrows-alt" aria-hidden="true"></span>
		</a>
	<iframe class="embed-responsive-item elem-fullscreen" src="D:/College/Capstone Project/Final/backend/Create-tutorials-from-text-file/scripts/static/downloads/abstract_summary.pdf#view=FitH&toolbar=0&navpanes=0&scrollbar=0&pagemode=bookmarks&page=2" allowfullscreen="" width="100%" height="auto">
	  {/* <!DOCTYPE html> */}
	  {/* <html><head></head><body style="height: 100%; width: 100%; overflow: hidden; margin:0px; background-color: rgb(82, 86, 89);"><embed name="4554881B4879FAE0AF3D5241613C0165" style="position:absolute; left: 0; top: 0;" width="100%" height="100%" src="about:blank" type="application/pdf" internalid="4554881B4879FAE0AF3D5241613C0165"></body></html> */}
	</iframe>
	</div>
	</div>
)
}
}

export default Sidebar;
