import React from 'react';
import './content.css';

class Content extends React.Component{
    constructor(props){
        super(props);
        this.state=props.heading;
    }
    render(){
      // <CustomTag level={data["level"]}>{data["heading"]}</CustomTag>
      // const CustomTag=({level}) => {return `<h${level}>`;}
      // let level = 3;
        const Content_data=({data}) =>(<div><p className={"class"+data["level"]}>{data["heading"]}</p><br></br>
        <p>{data["content"]}</p></div>);
        const TreeRecursive = ({ data }) => {
              if (data["children"].length == 0) {
                return <Content_data data={data}/>
              }
              else {
                return (
                    <div>
                  <Content_data data={data}/>
                  <br></br>
                  {data["children"].map((item) => (
                    <TreeRecursive data={item} />))};
                </div>
                );
              }
            }
        return(
            <TreeRecursive data={this.state}/>
        )
    }
}
export default Content;
