import React from 'react';

class Gallery extends React.Component{
    constructor(props){
        super(props);
        // this.get_pics=this.get_pics.bind(this);
        this.id=4;
        this.state={
            paths:[]
        };
    }
    componentDidMount(){
        console.log("hi")
        fetch('http://localhost:5000/picture-gallery', {
      method: 'POST',
      body:JSON.stringify({'id':this.id}),
      headers: new Headers({
        "content-type": "application/json"
      })
    }).then((response) => {
      response.json().then((body) => {
        this.setState({paths:body});
        for(var i=0;i<this.state.paths.length;i++){
            var path=this.paths[i].replace('\\','\\\\');
            this.paths[i]=<img src={path}></img>;
        }
        });
    })
    }
    render(){
        return (<div>
            <h1>Picture Gallery</h1>
            <h1>{this.state.paths}</h1>
        </div>)
    }
}

export default Gallery;
